package web.action;

import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;
import javax.servlet.http.HttpSession;

import org.apache.struts.action.Action;
import org.apache.struts.action.ActionForm;
import org.apache.struts.action.ActionForward;
import org.apache.struts.action.ActionMapping;

import service.IMotorCarService;

import common.exception.MotorbikeCarException;
import common.util.BeanFactory;

import dao.bean.OutRecord;

/** 
 * MyEclipse Struts
 * Creation date: 05-10-2008
 * 
 * XDoclet definition:
 * @struts.action input="/pages/car/backlist.jsp"
 * @struts.action-forward name="success" path="/pages/car/backrecord.jsp" redirect="true"
 * @struts.action-forward name="failure" path="/pages/car/backlist.jsp" redirect="true"
 */
public class BackRecordInfoAction extends Action {
	/*
	 * Generated Methods
	 */

	/** 
	 * Method execute
	 * @param mapping
	 * @param form
	 * @param request
	 * @param response
	 * @return ActionForward
	 */
	public ActionForward execute(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) {
		HttpSession session=request.getSession();
		long outid=Long.parseLong(request.getParameter("backid"));
		System.out.println(outid);
		IMotorCarService service=(IMotorCarService) BeanFactory.getBean("motorcarService");
		try {
			OutRecord record=service.findOutRecordById(outid);
			session.setAttribute("outrecord", record);
			return mapping.findForward("success");
		} catch (MotorbikeCarException e) {
			e.printStackTrace();
			session.setAttribute("message", e.getMessage());
			return mapping.findForward("failure");
		}
	}
}