package web.action;

import java.util.List;

import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;
import javax.servlet.http.HttpSession;

import org.apache.struts.action.Action;
import org.apache.struts.action.ActionForm;
import org.apache.struts.action.ActionForward;
import org.apache.struts.action.ActionMapping;

import service.IUserService;

import common.exception.UserException;
import common.util.BeanFactory;

/** 
 * MyEclipse Struts
 * Creation date: 05-11-2008
 * 
 * XDoclet definition:
 * @struts.action input="/pages/member/frame/rightframe.jsp"
 * @struts.action-forward name="success" path="/pages/member/driverlist.jsp" redirect="true"
 * @struts.action-forward name="failure" path="/pages/member/frame/rightframe.jsp" redirect="true"
 */
public class FindAllEmployeesAction extends Action {
	/*
	 * Generated Methods
	 */

	/** 
	 * Method execute
	 * @param mapping
	 * @param form
	 * @param request
	 * @param response
	 * @return ActionForward
	 */
	public ActionForward execute(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) {
		HttpSession session=request.getSession();
		IUserService service = (IUserService) BeanFactory
				.getBean("userService");
		try {
			List list=service.findAllDrivers();
			session.setAttribute("driverlist", list);
			return mapping.findForward("success");
		} catch (UserException e) {
			e.printStackTrace();
			session.setAttribute("message",e.getMessage());
			return mapping.findForward("failure");
		}
	}
}