package web.action;

import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;
import javax.servlet.http.HttpSession;

import org.apache.struts.action.Action;
import org.apache.struts.action.ActionForm;
import org.apache.struts.action.ActionForward;
import org.apache.struts.action.ActionMapping;

import common.exception.MotorbikeCarException;
import common.util.BeanFactory;

import service.IMotorCarService;

import dao.bean.ApplicationRecord;
import dao.bean.MotorCar;
import dao.bean.OutRecord;

/** 
 * MyEclipse Struts
 * Creation date: 05-10-2008
 * 
 * XDoclet definition:
 * @struts.action
 * @struts.action-forward name="success" path="/pages/car/outrecord.jsp" redirect="true"
 */
public class OutInfoAction extends Action {
	/*
	 * Generated Methods
	 */

	/** 
	 * Method execute
	 * @param mapping
	 * @param form
	 * @param request
	 * @param response
	 * @return ActionForward
	 */
	public ActionForward execute(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) {
		HttpSession session=request.getSession();
		long appid=Long.parseLong(request.getParameter("appid"));
		IMotorCarService service=(IMotorCarService) BeanFactory.getBean("motorcarService");
		try {
			ApplicationRecord record=service.findAppById(appid);
			session.setAttribute("apprecord", record);
			long capacity=record.getCapacity();
			System.out.println(capacity);
			MotorCar car=service.findBestCar(capacity);
			System.out.println(car.getCapacity());
			session.setAttribute("motorcar", car);
		} catch (MotorbikeCarException e) {
			e.printStackTrace();
			session.setAttribute("message", "对不起，没有找到合适的车辆");
			
		}
		return mapping.findForward("success");
	}
}